import React from "react"
import axios from "axios"
import "./Login.css";
import {useNavigate} from "react-router-dom";
import { MDBIcon } from 'mdb-react-ui-kit';

class Records extends React.Component{
    state = {
        customerName : "",
        loanID : "",
        amount : "",
        billNumber : "",
        paidStatus : "",
        posts : [],
    };
    componentDidMount = () =>{
        this.getRecords();
    }


    getRecords = () =>{
        axios.get("/api")
        .then((response)=>{
            const data = response.data;
            this.setState({posts : data});
            console.log("Data has been received");
        })
        .catch(()=>{
            console.log("Error receiving data");
        });
    }


    displayPosts=(posts)=>{
        if (!posts.length) return null;
        return posts.map((post, index) => (
          <div key={index}  className="table-container">
            <table className="table">
                <tr>
                    <td className="table-data"> <h3>{post.customerName}</h3></td>
                    <td className="table-data"> <h3>{post.loanID}</h3></td>
                    <td className="table-data"><h3>{post.amount}</h3></td>
                    <td className="table-data"><h3>{post.billNumber}</h3></td>
                    <td className="table-data"><h3>{post.paidStatus}</h3></td>
                    <td className="table-data"><h3>{post.verify}</h3></td>

                </tr>
            </table> 
          </div>
        ));
    }

    render(){
        console.log("State : ",this.state);

        const toCustomerDashboard = ()=>{
            this.props.navigate("/AdministratorDashboard");
        }

        return(

            <div className="Records">
                <div className="titleContainer">
                    <p className="title">LOAN RECORDS</p>
                </div>
            <div className="Record-container">
            <div className="Record">
                <div className="table-container">
                <table className="table">
                    <tr>
                        <td className="table-header"><h3>NAME OF CUSTOMER</h3></td>
                        <td className="table-header"><h3>LOAN ID (#)</h3></td>
                        <td className="table-header"><h3>AMOUNT (₹)</h3></td>
                        <td className="table-header"><h3>BILL NUMBER</h3></td>
                        <td className="table-header"><h3>PAID STATUS</h3></td>
                        <td className="table-header"><h3>VERIFY</h3></td>

                    </tr>
                </table>
                </div>
                {this.displayPosts(this.state.posts)}
                <button className="submitButton" onClick={toCustomerDashboard}>Back</button>
                </div>
                <div className="verificationPart">
                <div className="formContainerBox">
                <h1 className="header" id="loginTitle">ADD RECORD</h1>
                <div className="ormBox">
                <form id="formBox" >
                    <input type="text" id="customerName"
                      name="customerName" placeholder="Customer Name" className="inputField" autoComplete="off" />

                    <input type="text" id="loanID"
                      name="loanID" placeholder="LoanID" className="inputField" autoComplete="off" />

                    <input type="text" id="amount"
                     name="amount" placeholder="Amount" className="inputField" autoComplete="off"/>

                    <button  className="submitButton" >Add Loan Record</button>
                </form> 
                </div>
                </div>
                </div>
            </div>


            </div>
        )
    }
}
function WithNavigate(props){
    let navigate = useNavigate();
    return <Records {...props} navigate={navigate}/>
}


export default WithNavigate;
