import React from "react";
import jwt from "jsonwebtoken"
import {useEffect,useState} from "react";
import {useNavigate} from "react-router-dom";
import axios from "axios"
import "./Login.css"
import {ToastContainer} from "react-toastify";
import "react-toastify"

const CustomerDashboard = ()=>{
    const getBlogPost = ()=>{
        axios.get("https://localhost:27017/")
    }

    const navigate = useNavigate();
    async function populateQuote(){
        const req = await fetch("http://localhost:3001/api/quote",{
            headers : {
                "x-access-token" : localStorage.getItem("token"),
            },
        })
        const data = req.json();
        console.log(data); 
    }

    useEffect(()=>{
        const token = localStorage.getItem("token");
        if(token){
            const user = jwt.decode(token);
            if(!user){
                localStorage.removeItem("token");
                navigate("/Login");
            }
            else{
                populateQuote();
            }
        }
    },[])
    const [amount,setAmount] = useState("");
    const [interest,setInterest] = useState("");
    const [loanID,setLoanID] = useState("");

    const handleAmount = function(e){
        setAmount(e.target.value);
    }
    const handleInterest = function(e){
        setInterest(e.target.value);
    }
    const handleLoanID = function(e){
        setLoanID(e.target.value);
    }
    async function buyLoan(event){
        event.preventDefault();
        const response = await fetch("http://localhost:3000/api/buyLoan",{
            method:'POST',
            headers:{
                'Content-Type' : 'application/json',
            },
            body : JSON.stringify({
                amount,
                interest,
                loanID
            }),
        })
        const data = await response.json();
        console.log(data);
        if(data.status=="ok"){
            console.log("stored");
        }
    }
    return (
        <div className="CustomerDashboard">
            <div className="display">
                <div className="formContainerBox">
                <h1 className="header" id="loginTitle">BUY LOAN</h1>
                <div className="formBox">
                <form id="formBox" onSubmit={buyLoan}>
                    <input type="text" id="amount" 
                    onChange={handleAmount}  name="amount" placeholder="Amount" className="inputField" autoComplete="off" />

                    <input type="text" id="interest" 
                    onChange={handleInterest} name="interest" placeholder="Interest" className="inputField" autoComplete="off"/>

                    <input type="text" id="loanID" 
                    onChange={handleLoanID} name="loanID" placeholder="Loan ID" className="inputField" autoComplete="off" />

                    <button  className="submitButton" >Buy Loan</button>
                </form> 
                </div>


                </div>
                <div className="formContainerBox">
                <h1 className="header" id="loginTitle">PAY LOAN</h1>
                <div className="formBox">
                <form id="formBox">
                    <input type="text" id="name"  name="name" placeholder="Amount" className="inputField" autoComplete="off" 
                     />
                    {/* <p className="errorMessage">{formErrors.name}</p> */}
                    {/* <input type="text" name="lastName" placeholder="Last Name" className="inputField" /> */}
                    <input type="email" id="email" name="email" placeholder="Interest" className="inputField" autoComplete="off"
                     />
                    {/* <p className="errorMessage">{formErrors.email}</p> */}
                    {/* <input type="text" name="username" placeholder="Username" className="inputField" /> */}
                    <input type="password" id="password" name="password" placeholder="Loan ID" className="inputField" autoComplete="off" 
                     />
                    {/* <p className="errorMessage">{formErrors.password}</p> */}
                    <button  className="submitButton" >Pay Loan</button>
                    {/* <input type="submit" id="button" value="Register"></input> */}
                    {/* <button class="submitButton">Login as admin</button> */}    
                    {/* <p><a href="/Users/logee/Documents/Codes/miniProject/finance-system/login.html" className="link">Already Registerd? Login</a></p> */}
                </form> 
                </div>


                </div>
            </div>
        </div>
    )
}

export default CustomerDashboard;
