    //         console.log(formValues);    
    //     }
    // },[formErrors]);


    // Authentication

    const [email,setEmail] = useState("");
    const [password,setPassword] = useState("");

    const handleEmail = function(e){
        setEmail(e.target.value);
    }
    const handlePassword = function(e){
        setPassword(e.target.value);
    }
    async function loginUser(event){
        event.preventDefault();
        const {email,password} = req.body;
        if(!email || !password)
            return res.json({status : "noInput"});
        const response  = await fetch('http://localhost:3001/api/login/',{
            method:'POST',
            headers:{
                'Content-Type' : 'application/json',
            },
            body : JSON.stringify({
                email,
                password,
            }),
        })
        console.log(response);
        const data = await response.json();
        try{
            if(data.user){
                localStorage.setItem("token",data.user);
                window.location.href = "./CustomerDashboard"; 
            }else{
                console.log("Invalid Credentials");
                toast.error("Invalid Credentials");
            }
        }
        catch(e){
            console.error(e);
        }
        console.log(data);
    }
    const navigate = useNavigate();
    const handleAdminLogin = () =>{  
        navigate("/AdminLogin");
    }
    const handleRegister = () => {
        navigate("/Register");
    }
    return(
        <div className="Login">
            <div className="formContainer">
            <h1 className="header" id="loginTitle">CUSTOMER LOGIN <span><br/><span id="l1" >KRISHNA </span>
                <span id="l2">FINANCE</span></span></h1>
            <div className="form">
            <br />
            <form id="form" onSubmit={loginUser}>
                <input type="email" name="email" placeholder="Email" className="inputField" 
                 onChange={handleEmail}/>
                {/* <p className="errorMessage">{formErrors.email}</p> */}
                <input type="password" name="password" placeholder="Password" className="inputField" 
                 onChange={handlePassword}/>
                {/* <p className="errorMessage">{formErrors.password}</p> */}
                <button className="submitButton">Login</button>
                {/* <button class="submitButton">Login as admin</button> */}   
                <p className="link" onClick={handleAdminLogin}>Login as admin?</p>
                <p>
                <p className="link" onClick={handleRegister}>New User? Register</p>
                </p>
            </form>
            </div>
            </div>
        </div>
    );
}

export default Login;
